mapper.delete:
delete from pullRequest where repositoryId = #{pullRequest.repositoryId} and agentName = #{pullRequest.agentName}
                          and branch = #{pullRequest.branch} and targetId = #{pullRequest.targetId} and
                              targetBranch = #{pullRequest.targetBranch}

mapper.update:
update pullRequest set (isClosed = #{pullRequest.isClosed},status = #{pullRequest.status})
                   where repositoryId = #{pullRequest.repositoryId} and agentName = #{pullRequest.agentName}
                        and branch = #{pullRequest.branch} and targetId = #{pullRequest.targetId} and
                        targetBranch = #{pullRequest.targetBranch}
